RSpec.describe "/assemblies", type: :request do
  
  # This should return the minimal set of attributes required to create a valid
  # Assembly. As you add validations to Assembly, be sure to
  # adjust the attributes here as well.
  let!(:assembly) {create(:assembly)}
  let!(:assembly_attributes) {attributes_for(:assembly)}

  describe "GET /index" do
    context "when the part exists"  do
      before do
        get "/assemblies/"
      end

      it "renders a successful response" do
        expect(response).to have_http_status(:success)
      end

      it "renders assembly name" do
        expect(response.body).to include(assembly.name)
      end

      it "renders part name" do
        expect(response.body).to include(assembly.parts[0].name)
        end

      it "renders book titulo" do
        expect(response.body).to include(assembly.book.titulo)
      end
    end
  end

  describe "GET /show" do
    context "when the account exists"  do
      before do
        get "/assemblies/", params: {id:assembly.id}
      end

      it "renders a successful response" do
        expect(response).to have_http_status(:success)
      end

      it "renders assembly name" do
        expect(response.body).to include(assembly.name)
      end

      it "renders part name" do
        expect(response.body).to include(assembly.parts[0].name)
      end

      it "renders book titulo" do
        expect(response.body).to include(assembly.book.titulo)
      end
    end

    context "when the assembly doesn't exists" do
      let!(:assembly_error) {build(:assembly)}
      before do
        get "/assemblies/", params: {id:assembly_error.id}
      end

      it "not renders account number" do
        expect(response.body).to_not include(assembly_error.name)
      end
    end
  end

  describe "GET /new" do
    it "renders a successful response" do
      get "/assemblies/new"
      expect(response).to be_successful
    end
  end

  describe "GET /edit" do
    context "when the assembly exists"  do
      before do
        get "/assemblies/#{assembly.id}/edit"
      end

      it "renders a successful response" do
        expect(response).to be_successful
      end

      it "renders assembly name" do
        expect(response.body).to include(assembly.name)
      end

      it "renders part name" do
        expect(response.body).to include(assembly.parts[0].name)
      end

      it "renders book titulo" do
        expect(response.body).to include(assembly.book.titulo)
      end
    end
  end

  describe "POST /create" do
    let!(:assembly_) {build(:assembly)}
    context "with valid parameters" do
      before do
        post "/assemblies/", params: { assembly: { name: assembly_.name, parts: [assembly_.parts[0].id, assembly_.parts[1].id], book_id: assembly.book.id } }
      end

      it "creates a new assembly" do
        expect(response).to have_http_status(:redirect)
      end

      it "redirects to the created assemblies" do
        get "/assemblies", params: { id: assembly.id }
        expect(response.body).to include("Assembly was successfully created.")
      end
    end

    context "with valid parameters in API" do

      before do
        post "/assemblies.json", params: { assembly: { name: assembly_.name, parts: [assembly_.parts[0].id, assembly_.parts[1].id], book_id: assembly.book.id } }
      end

      it "creates a new Author" do
        expect(response).to have_http_status(:created)
      end

      it "the created author message" do
        expect(response.body).to include("Assembly #{assembly_.name} was successfully created.")
      end
    end
  end

  describe "PATCH /update" do
    context "with valid parameters" do
      before do
        patch "/assemblies/#{assembly.id}", params: { assembly: { name: assembly.name, parts: [assembly.parts[0].id, assembly.parts[1].id], book_id: assembly.book.id } }
      end

      it "redirects to the book" do
        expect(response).to have_http_status(:redirect)
      end

      it "redirects to the update assembly" do
        get "/assemblies", params: { id: assembly.id }
        expect(response.body).to include("Assembly was successfully updated.")
      end

      it "renders assembly name" do
        get "/assemblies", params: { id: assembly.id }
        expect(response.body).to include(assembly.name)
      end

      it "renders part name" do
        get "/assemblies", params: { id: assembly.id }
        expect(response.body).to include(assembly.parts[0].name)
      end

      it "renders book titulo" do
        get "/assemblies", params: { id: assembly.id }
        expect(response.body).to include(assembly.book.titulo)
      end
    end

    context "with valid parameters in API" do
      before do
        put "/assemblies/#{assembly.id}.json", params: { assembly: { name: assembly.name, parts: [assembly.parts[0].id, assembly.parts[1].id], book_id: assembly.book.id } }
      end

      it "redirects to the author" do
        expect(response).to have_http_status(:ok)
      end

      it "redirects to the update author" do
        expect(response.body).to include("Assembly #{assembly.name} was successfully updated.")
      end
    end

  end

  describe "DELETE /destroy" do
    context "with valid parameters" do
      before do
        delete "/assemblies/#{assembly.id}"
      end

      it "destroys the requested parts" do
        expect(response).to have_http_status(:redirect)
      end

      it "redirects to the delete author" do
        get "/assemblies", params: { id: assembly.id }
        expect(response.body).to include("Assembly was successfully destroyed.")
      end
    end

    context "with valid parameters in API" do
      before do
        delete "/assemblies/#{assembly.id}.json"
      end

      it "destroys the requested author" do
        expect(response).to have_http_status(:ok)
      end

      it "redirects to the delete author" do
        expect(response.body).to include("Assembly #{assembly.name} was successfully destroyed.")
      end
    end
  end
end
